import React, { useState, useRef, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MicrophoneIcon, 
  StopIcon, 
  PaperAirplaneIcon,
  SpeakerWaveIcon,
  SpeakerXMarkIcon,
  SparklesIcon,
  ChatBubbleLeftEllipsisIcon,
  SunIcon,
  MoonIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';
import TextInput from './TextInput';

const SimplifiedChatInterface = ({ user, darkMode, setDarkMode, sessionId }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isBotSpeaking, setIsBotSpeaking] = useState(false);
  const [currentTranscript, setCurrentTranscript] = useState('');
  const [recordingTimer, setRecordingTimer] = useState(0);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const messagesEndRef = useRef(null);
  const audioRef = useRef(null);
  const recordingIntervalRef = useRef(null);
  const streamRef = useRef(null);

  const suggestions = [
    "Tell me a story",
    "Sing a song", 
    "What's a fun fact?",
    "Let's play a game",
    "Help me learn something"
  ];

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const startRecording = async () => {
    try {
      console.log('Starting recording...');

      // Reset states
      setCurrentTranscript('');
      setRecordingTimer(0);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          sampleRate: 16000,
          channelCount: 1,
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });

      streamRef.current = stream;

      // Use higher quality audio recording
      const options = {
        mimeType: 'audio/webm;codecs=opus',
        audioBitsPerSecond: 128000
      };

      // Fallback for browsers that don't support webm
      if (!MediaRecorder.isTypeSupported(options.mimeType)) {
        options.mimeType = 'audio/wav';
      }

      mediaRecorderRef.current = new MediaRecorder(stream, options);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
          console.log('Audio chunk received:', event.data.size, 'bytes');
        }
      };

      mediaRecorderRef.current.onstop = () => {
        console.log('Recording stopped, processing audio...');
        const audioBlob = new Blob(audioChunksRef.current, { 
          type: mediaRecorderRef.current.mimeType 
        });
        console.log('Audio blob created:', audioBlob.size, 'bytes, type:', audioBlob.type);
        sendVoiceMessage(audioBlob);

        // Clean up stream
        if (streamRef.current) {
          streamRef.current.getTracks().forEach(track => track.stop());
          streamRef.current = null;
        }
      };

      // Start recording with chunks every 100ms for better quality
      mediaRecorderRef.current.start(100);
      setIsRecording(true);

      // Add a live transcript message
      const liveMessage = {
        id: `recording-${Date.now()}`,
        type: 'user',
        content: '🎤 Recording... (hold to continue)',
        isVoice: true,
        isLive: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, liveMessage]);

      // Start recording timer
      recordingIntervalRef.current = setInterval(() => {
        setRecordingTimer(prev => prev + 1);
      }, 1000);

      console.log('Recording started successfully');

    } catch (error) {
      console.error('Microphone access error:', error);
      toast.error(`Microphone access denied: ${error.message}`);
    }
  };

  const stopRecording = () => {
    console.log('Stopping recording...');

    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setRecordingTimer(0);

      // Clear recording timer
      if (recordingIntervalRef.current) {
        clearInterval(recordingIntervalRef.current);
        recordingIntervalRef.current = null;
      }

      // Remove live recording message
      setMessages(prev => prev.filter(msg => !msg.isLive));

      console.log('Recording stop initiated');
    }
  };

  const sendVoiceMessage = async (audioBlob) => {
    console.log('Sending voice message, blob size:', audioBlob.size, 'type:', audioBlob.type);

    // Validate audio blob
    if (!audioBlob || audioBlob.size === 0) {
      console.error('Invalid audio blob');
      toast.error('Recording failed - no audio data');
      return;
    }

    // Create temporary user message for processing
    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: '🎤 Processing voice...',
      isVoice: true,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      // Convert audio blob to ArrayBuffer first for better handling
      const arrayBuffer = await audioBlob.arrayBuffer();
      const uint8Array = new Uint8Array(arrayBuffer);
      const base64Audio = btoa(String.fromCharCode(...uint8Array));

      console.log('Audio converted to base64, length:', base64Audio.length);

      // Create form data with proper content type
      const formData = new FormData();
      formData.append('session_id', sessionId);
      formData.append('user_id', user.id);
      formData.append('audio_base64', base64Audio);

      console.log('Sending request to voice endpoint...');

      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/voice/process_audio`, {
        method: 'POST',
        body: formData
      });

      const data = await response.json();
      console.log('Voice processing response:', data);

      if (response.ok && data.status === 'success') {
        // Update user message with transcript
        setMessages(prev => prev.map(msg => 
          msg.id === userMessage.id 
            ? { ...msg, content: data.transcript || 'Voice message processed' }
            : msg
        ));

        // Add AI response
        const aiMessage = {
          id: Date.now() + 1,
          type: 'ai',
          content: data.response_text || 'I heard you!',
          audioData: data.response_audio,
          contentType: data.content_type,
          metadata: data.metadata,
          timestamp: new Date()
        };

        setMessages(prev => [...prev, aiMessage]);

        // Auto-play AI response if available
        if (data.response_audio) {
          playAudio(data.response_audio);
        }

        toast.success('Voice message processed!');
      } else {
        throw new Error(data.detail || data.message || 'Voice processing failed');
      }

    } catch (error) {
      console.error('Voice message error:', error);
      toast.error(`Voice processing failed: ${error.message}`);

      // Update user message to show error
      setMessages(prev => prev.map(msg => 
        msg.id === userMessage.id 
          ? { ...msg, content: '❌ Voice processing failed - try again' }
          : msg
      ));
    } finally {
      setIsLoading(false);
    }
  };

  const sendTextMessage = async (messageText) => {
    if (!messageText || !messageText.trim() || isLoading) return;

    const messageContent = messageText.trim();

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: messageContent,
      isVoice: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/conversations/text`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          session_id: sessionId,
          user_id: user.id,
          message: messageContent
        })
      });

      const data = await response.json();

      if (response.ok) {
        const aiMessage = {
          id: Date.now() + 1,
          type: 'ai',
          content: data.response_text,
          audioData: data.response_audio,
          contentType: data.content_type,
          metadata: data.metadata,
          timestamp: new Date()
        };

        setMessages(prev => [...prev, aiMessage]);

        // Auto-play AI response
        if (data.response_audio) {
          playAudio(data.response_audio);
        }
      } else {
        throw new Error(data.detail || 'Failed to send message');
      }
    } catch (error) {
      toast.error('Failed to send message');
      console.error('Text message error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const playAudio = (base64Audio) => {
    if (audioRef.current) {
      audioRef.current.pause();
    }

    const audioBlob = new Blob([Uint8Array.from(atob(base64Audio), c => c.charCodeAt(0))], { type: 'audio/wav' });
    const audioUrl = URL.createObjectURL(audioBlob);

    audioRef.current = new Audio(audioUrl);
    audioRef.current.play();
    setIsPlaying(true);
    setIsBotSpeaking(true);

    audioRef.current.onended = () => {
      setIsPlaying(false);
      setIsBotSpeaking(false);
      URL.revokeObjectURL(audioUrl);
    };
  };

  const stopAudio = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
      setIsPlaying(false);
      setIsBotSpeaking(false);
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const handleMicPress = (e) => {
    e.preventDefault();
    console.log('Mic button pressed');
    if (!isRecording && !isLoading) {
      startRecording();
    }
  };

  const handleMicRelease = (e) => {
    e.preventDefault();
    console.log('Mic button released');
    if (isRecording) {
      stopRecording();
    }
  };

  // Handle keyboard interactions
  const handleMicKeyDown = (e) => {
    if (e.key === ' ' || e.key === 'Enter') {
      e.preventDefault();
      handleMicPress(e);
    }
  };

  const handleMicKeyUp = (e) => {
    if (e.key === ' ' || e.key === 'Enter') {
      e.preventDefault();
      handleMicRelease(e);
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (recordingIntervalRef.current) {
        clearInterval(recordingIntervalRef.current);
      }
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  // Dynamic background based on bot speaking
  const getBackgroundClass = () => {
    const base = darkMode 
      ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900'
      : 'bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50';

    if (isBotSpeaking) {
      return darkMode 
        ? 'bg-gradient-to-br from-purple-900 via-pink-900 to-orange-900'
        : 'bg-gradient-to-br from-yellow-100 via-orange-100 to-pink-100';
    }

    return base;
  };

  // Animated Bot Avatar Component
  const BotAvatar = () => (
    <div className="flex flex-col items-center justify-center h-full p-8">
      <motion.div
        className={`relative w-48 h-48 rounded-full flex items-center justify-center mb-6 ${
          darkMode ? 'bg-gradient-to-br from-blue-600 to-purple-700' : 'bg-gradient-to-br from-blue-500 to-purple-600'
        }`}
        animate={{
          scale: isBotSpeaking ? [1, 1.05, 1] : [1, 1.02, 1],
        }}
        transition={{
          scale: { duration: 2, repeat: Infinity, ease: "easeInOut" },
        }}
      >
        <motion.div 
          className="absolute inset-4 rounded-full bg-white/20 backdrop-blur-sm"
          animate={{
            opacity: isBotSpeaking ? [0.3, 0.7, 0.3] : [0.2, 0.4, 0.2]
          }}
          transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
        />

        {/* Eyes */}
        <div className="flex space-x-6">
          <motion.div 
            className="w-6 h-12 bg-white rounded-full"
            animate={{
              scaleY: isBotSpeaking ? [1, 0.3, 1] : [1, 0.8, 1]
            }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          />
          <motion.div 
            className="w-6 h-12 bg-white rounded-full"
            animate={{
              scaleY: isBotSpeaking ? [1, 0.3, 1] : [1, 0.8, 1]
            }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut", delay: 0.1 }}
          />
        </div>

        {/* Voice Rings */}
        <AnimatePresence>
          {(isBotSpeaking || isRecording) && (
            <>
              <motion.div
                className="absolute inset-0 rounded-full border-2 border-white/30"
                initial={{ scale: 1, opacity: 0.6 }}
                animate={{ scale: 1.5, opacity: 0 }}
                exit={{ opacity: 0 }}
                transition={{ duration: 1.5, repeat: Infinity, ease: "easeOut" }}
              />
              <motion.div
                className="absolute inset-0 rounded-full border-2 border-white/20"
                initial={{ scale: 1, opacity: 0.4 }}
                animate={{ scale: 2, opacity: 0 }}
                exit={{ opacity: 0 }}
                transition={{ duration: 2, repeat: Infinity, ease: "easeOut", delay: 0.3 }}
              />
            </>
          )}
        </AnimatePresence>
      </motion.div>

      <motion.div
        className={`text-center ${darkMode ? 'text-white' : 'text-gray-800'}`}
        animate={{ opacity: [0.7, 1, 0.7] }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      >
        <h2 className="text-2xl font-bold mb-2">Hi {user?.name || 'there'}! 👋</h2>
        <p className="text-lg opacity-80">
          {isRecording ? `🎤 Recording... ${recordingTimer}s` : 
           isBotSpeaking ? 'Speaking...' :
           'Press and hold the mic to talk!'}
        </p>
      </motion.div>

      <div className="mt-6 text-4xl">
        {isRecording && `🎤 ${recordingTimer}s`}
        {isBotSpeaking && '🗣️'}
        {!isRecording && !isBotSpeaking && '😊'}
      </div>
    </div>
  );

  return (
    <div className={`h-full ${getBackgroundClass()} transition-all duration-1000`}>
      {/* Modern 2-Panel Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-2 h-full">
        {/* Left Panel - Animated Bot Avatar */}
        <div className="flex items-center justify-center min-h-96 lg:min-h-full">
          <BotAvatar />
        </div>

        {/* Right Panel - Live Transcript */}
        <div className={`h-full min-h-96 lg:min-h-full ${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'} border-l ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
          {/* Header */}
          <div className={`flex-shrink-0 p-4 border-b ${darkMode ? 'border-gray-700 bg-gray-900' : 'border-gray-200 bg-gray-50'}`}>
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <ChatBubbleLeftEllipsisIcon className={`w-6 h-6 ${darkMode ? 'text-blue-400' : 'text-blue-600'}`} />
                <h3 className="text-lg font-semibold">Chat with Buddy</h3>
              </div>

              <button
                onClick={() => setDarkMode(!darkMode)}
                className={`p-2 rounded-full transition-colors ${
                  darkMode ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600' : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {darkMode ? <SunIcon className="w-5 h-5" /> : <MoonIcon className="w-5 h-5" />}
              </button>
            </div>
          </div>

          {/* Messages Area */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4" style={{ height: 'calc(100% - 160px)' }}>
            {messages.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-6xl mb-4">🎙️</div>
                <h4 className={`text-lg font-semibold mb-2 ${darkMode ? 'text-gray-200' : 'text-gray-800'}`}>
                  Start Talking!
                </h4>
                <p className={`${darkMode ? 'text-gray-400' : 'text-gray-600'} mb-6`}>
                  Your conversation will appear here in real-time
                </p>

                <div className="flex flex-col space-y-2">
                  {suggestions.map((suggestion, index) => (
                    <motion.button
                      key={index}
                      onClick={() => sendTextMessage(suggestion)}
                      className={`px-4 py-2 rounded-full text-sm transition-colors ${
                        darkMode 
                          ? 'bg-blue-900 text-blue-200 hover:bg-blue-800' 
                          : 'bg-blue-50 text-blue-600 hover:bg-blue-100'
                      }`}
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      {suggestion}
                    </motion.button>
                  ))}
                </div>
              </div>
            ) : (
              <AnimatePresence>
                {messages.map((message) => (
                  <motion.div
                    key={message.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className="max-w-sm">
                      <div
                        className={`px-4 py-3 rounded-2xl ${
                          message.type === 'user'
                            ? darkMode 
                              ? 'bg-gradient-to-r from-blue-600 to-purple-700 text-white'
                              : 'bg-gradient-to-r from-blue-500 to-purple-600 text-white'
                            : darkMode
                            ? 'bg-gray-700 text-gray-100'
                            : 'bg-gray-100 text-gray-900'
                        }`}
                      >
                        <div className="flex items-start space-x-2">
                          {message.type === 'ai' && (
                            <span className="text-xl">🤖</span>
                          )}
                          {message.type === 'user' && (
                            <span className="text-xl">👶</span>
                          )}
                          <p className="text-sm leading-relaxed">{message.content}</p>
                        </div>
                      </div>

                      <div className="flex items-center justify-between mt-1 px-2">
                        <span className={`text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                          {formatTime(message.timestamp)}
                        </span>

                        {message.audioData && (
                          <button
                            onClick={() => playAudio(message.audioData)}
                            className={`p-1 rounded transition-colors ${
                              darkMode 
                                ? 'text-blue-400 hover:text-blue-300 hover:bg-gray-700' 
                                : 'text-blue-500 hover:text-blue-600 hover:bg-blue-50'
                            }`}
                          >
                            <SpeakerWaveIcon className="w-4 h-4" />
                          </button>
                        )}
                      </div>
                    </div>
                  </motion.div>
                ))}
              </AnimatePresence>
            )}

            {/* Loading Animation */}
            {isLoading && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="flex justify-start"
              >
                <div className={`px-4 py-3 rounded-2xl ${
                  darkMode ? 'bg-gray-700' : 'bg-gray-100'
                }`}>
                  <div className="flex items-center space-x-2">
                    <span className="text-xl">🤖</span>
                    <div className="flex space-x-1">
                      <div className={`w-2 h-2 rounded-full animate-bounce ${
                        darkMode ? 'bg-blue-400' : 'bg-blue-500'
                      }`}></div>
                      <div className={`w-2 h-2 rounded-full animate-bounce ${
                        darkMode ? 'bg-blue-400' : 'bg-blue-500'
                      }`} style={{ animationDelay: '0.1s' }}></div>
                      <div className={`w-2 h-2 rounded-full animate-bounce ${
                        darkMode ? 'bg-blue-400' : 'bg-blue-500'
                      }`} style={{ animationDelay: '0.2s' }}></div>
                    </div>
                    <span className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                      Thinking...
                    </span>
                  </div>
                </div>
              </motion.div>
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className={`flex-shrink-0 p-4 border-t ${darkMode ? 'border-gray-700 bg-gray-900' : 'border-gray-200 bg-gray-50'}`}>
            <div className="flex items-center justify-between space-x-4">
              <div className="flex-1">
                <TextInput 
                  onSendMessage={sendTextMessage} 
                  isLoading={isLoading}
                  darkMode={darkMode}
                />
              </div>

              {/* Microphone Button */}
              <button
                onMouseDown={handleMicPress}
                onMouseUp={handleMicRelease}
                onTouchStart={handleMicPress}
                onTouchEnd={handleMicRelease}
                className={`p-3 rounded-full transition-all duration-200 ${
                  isRecording 
                    ? 'bg-red-500 text-white scale-110' 
                    : darkMode
                    ? 'bg-blue-600 text-white hover:bg-blue-500'
                    : 'bg-blue-500 text-white hover:bg-blue-600'
                } ${isLoading ? 'opacity-50' : ''}`}
                disabled={isLoading}
              >
                {isRecording ? (
                  <StopIcon className="w-6 h-6" />
                ) : (
                  <MicrophoneIcon className="w-6 h-6" />
                )}
              </button>
            </div>

            <p className={`text-center text-sm mt-2 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
              {isRecording ? '🎤 Release to stop recording' : '🎤 Press and hold to speak'}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SimplifiedChatInterface;
